public class Order\n{\n    private readonly IList<OrderLine> lines = new List<OrderLine>();\n    private readonly ReadOnlyCollection<OrderLine> readOnlyLines;\n\n    public Order()\n    {\n        readOnlyLines = new ReadOnlyCollection<OrderLine>(lines);\n    }\n\n    public ReadOnlyCollection<OrderLine> Lines => readOnlyLines;\n\n    public void AddLine(OrderLine line)\n    {\n        lines.Add(line);\n    }\n}