using OfficeOpenXml;\n\npublic class ExcelToSql\n{\n    public static void GenerateSql(string excelFile, string tableName)\n    {\n        using (ExcelPackage package = new ExcelPackage(new FileInfo(excelFile)))\n        {\n            ExcelWorksheet worksheet = package.Workbook.Worksheets[1];\n\n            // Read the header row\n            string[] headers = new string[worksheet.Dimension.Columns];\n            for (int i = 1; i <= worksheet.Dimension.Columns; i++)\n            {\n                headers[i - 1] = worksheet.Cells[1, i].Value.ToString();\n            }\n\n            // Generate create table statement\n            StringBuilder createTable = new StringBuilder();\n            createTable.AppendLine($"CREATE TABLE {tableName} (");\n            foreach (string header in headers)\n            {\n                createTable.AppendLine($"{header} VARCHAR(255),");\n            }\n            createTable.AppendLine(");");\n\n            Console.WriteLine(createTable.ToString());\n\n            // Generate insert statements\n            for (int row = 2; row <= worksheet.Dimension.Rows; row++)\n            {\n                string[] fields = new string[worksheet.Dimension.Columns];\n                for (int i = 1; i <= worksheet.Dimension.Columns; i++)\n                {\n                    fields[i - 1] = worksheet.Cells[row, i].Value.ToString();\n                }\n\n                StringBuilder insert = new StringBuilder();\n                insert.AppendLine($"INSERT INTO {tableName} VALUES (");\n                foreach (string field in fields)\n                {\n                    insert.AppendLine($"'{field}',");\n                }\n                insert.AppendLine(");");\n\n                Console.WriteLine(insert.ToString());\n            }\n        }\n    }\n}