using System.Xml;\nusing System.Xml.XPath;\n\n// Create an XmlReader with namespace support\nvar xmlReader = XmlReader.Create(new StringReader(xmldocument));\n\n// Create an XPathNavigator with namespace support\nvar navigator = xmlReader.CreateNavigator();\n\n// Bind the namespace prefix to the namespace URI\nvar namespaceManager = new XmlNamespaceManager(navigator.NameTable);\nnamespaceManager.AddNamespace("ns", "uri");\n\n// Use the namespace prefix in the XPath expression\nvar xpath = "//ns:foo";\nvar nodes = navigator.Select(xpath, namespaceManager);