using System;\nusing System.Security.Cryptography;\n\npublic class Program\n{\n    public static byte[] GenerateTrueRandomNumber(int size)\n    {\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            var randomNumber = new byte[size];\n            rng.GetBytes(randomNumber);\n            return randomNumber;\n        }\n    }\n\n    public static void Main()\n    {\n        var randomNumber = GenerateTrueRandomNumber(32);\n        Console.WriteLine(BitConverter.ToString(randomNumber));\n    }\n}