var discoveryClient = new DiscoveryClient(new UdpDiscoveryEndpoint());\n\n// Find the service\nvar services = discoveryClient.Find(new FindCriteria(typeof(IMyService)));\n\n// Use the service\nvar service = services.FirstOrDefault();\nif (service != null)\n{\n    var channel = new ChannelFactory<IMyService>(new BasicHttpBinding(), service.Address);\n    var client = channel.CreateChannel();\n    client.MyMethod();\n}