public List<MyObject> GetSortedReports(List<MyObject> returnReports, string sortColumn, int pageSize, int pageNumber)\n{\n    var propertyInfo = typeof(MyObject).GetProperty(sortColumn);\n    if (propertyInfo == null)\n    {\n        throw new ArgumentException("Invalid sort column", nameof(sortColumn));\n    }\n\n    return returnReports.OrderBy(x => propertyInfo.GetValue(x))\n                        .Skip(pageSize * (pageNumber - 1))\n                        .Take(pageSize)\n                        .ToList();\n}