using System.Data.SqlServerCe;\n\n// Create a connection to the SQL Server CE database\nstring connectionString = "Data Source=\\MobileDevice\\Path\\To\\Database.sdf";\nusing (SqlServerCeConnection connection = new SqlServerCeConnection(connectionString))\n{\n    connection.Open();\n\n    // Create a command to select data from the database\n    SqlServerCeCommand command = new SqlServerCeCommand("SELECT * FROM YourTable", connection);\n\n    // Read the data and export it to CSV\n    using (SqlServerCeDataReader reader = command.ExecuteReader())\n    {\n        while (reader.Read())\n        {\n            // Write the data to a CSV file\n            string csvLine = string.Join(",", reader.GetValues());\n            File.AppendAllText("output.csv", csvLine + Environment.NewLine);\n        }\n    }\n}