using HtmlAgilityPack;\n\npublic static string TruncateHtml(string text, int length)\n{\n    var doc = new HtmlDocument();\n    doc.LoadHtml(text);\n    var textBuilder = new StringBuilder();\n    foreach (var node in doc.DocumentNode.Descendants())\n    {\n        if (node.NodeType == HtmlNodeType.Text)\n        {\n            textBuilder.Append(node.InnerText);\n        }\n        else if (node.NodeType == HtmlNodeType.Element)\n        {\n            textBuilder.Append(node.OuterHtml);\n        }\n    }\n    var truncatedText = textBuilder.ToString().Substring(0, length);\n    truncatedText = truncatedText.TrimEnd(' ', '.', ',', '!', '?', ';', ':');\n    return truncatedText + "...";\n}\n\nstring text = "Hello, my <strong>name</strong> is <em>Sam</em>. I&acute;m a web developer.";\nConsole.WriteLine(TruncateHtml(text, 26));