private Dictionary<Widget, int> tabLabels = new Dictionary<Widget, int>();\n\nvoid CreateTabLabel(Widget tabContent)\n{\n    Widget tabLabel = new HBox();\n    Button closeButton = new Button("Close");\n    closeButton.Clicked += OnButtonClicked;\n    tabLabel.PackStart(closeButton, true, true, 0);\n    int pageNumber = myNotebook.AppendPage(tabContent, tabLabel);\n    tabLabels[closeButton] = pageNumber;\n}\n\nvoid OnButtonClicked(object sender, EventArgs args)\n{\n    Button button = (Button)sender;\n    int pageNumber = tabLabels[button];\n    // Use the pageNumber as needed\n}