using Microsoft.EntityFrameworkCore;\nusing System.Data.Odbc;\n\npublic class AccessDbContext : DbContext\n{\n    public AccessDbContext(DbContextOptions<AccessDbContext> options) : base(options) { }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseOdbc(@"DRIVER={Microsoft Access Driver (*.mdb, *.accdb)};DBQ=C:\Path\To\Your\Access\Database.accdb");\n    }\n\n    // Define your DbSets here (e.g., one for each table)\n    public DbSet<YourTable1> YourTable1 { get; set; }\n    public DbSet<YourTable2> YourTable2 { get; set; }\n    // ...\n}