using System.Text.RegularExpressions;\nusing System.Xml;\nusing System.Xml.XPath;\n\n// Load the XML document\nXmlDocument doc = new XmlDocument();\ndoc.Load("example.xml");\n\n// Create an XPath navigator\nXPathNavigator nav = doc.CreateNavigator();\n\n// Compile a regular expression\nRegex regex = new Regex("pattern");\n\n// Select elements based on the regular expression\nXPathExpression expr = nav.Compile("//*[text()[regex.IsMatch(., 'pattern')]]");\n\n// Evaluate the XPath expression\nXPathNodeIterator iterator = nav.Select(expr);\n\n// Iterate over the selected elements\nwhile (iterator.MoveNext())\n{\n    // Process the selected element\n    Console.WriteLine(iterator.Current.OuterXml);\n}