// Create a new AppDomain\nAppDomain oldDomain = AppDomain.CreateDomain("OldDomain");\n\n// Load the older version of the assembly into the new AppDomain\noldDomain.Load("OldVersionAssembly");\n\n// Get the type of the class you need to work with\nType oldType = oldDomain.GetType("OldVersionAssembly.OldClass");\n\n// Create an instance of the class in the new AppDomain\nobject oldInstance = oldDomain.CreateInstance(oldType);\n\n// Do your data migration work...\n\n// Unload the AppDomain when you're done\nAppDomain.Unload(oldDomain);