public partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n\n        pathGeometry.Changed += PathGeometry_Changed;\n        UpdateTextPath();\n    }\n\n    private void PathGeometry_Changed(object sender, EventArgs e)\n    {\n        UpdateTextPath();\n    }\n\n    private void UpdateTextPath()\n    {\n        var text = textBlock.Text;\n        var textPath = new TextPath(pathGeometry) { Text = text };\n        textBlock.Text = "";\n        textBlock.Inlines.Add(textPath);\n    }\n}