using Devart.Data.Oracle;\nusing Devart.Data.Linq;\n\npublic class MyContext : DataContext\n{\n    public MyContext() : base(new OracleConnection("Data Source=//localhost:1521/ORCL;User Id=MY_USER;Password=MY_PASSWORD;"))\n    {\n    }\n\n    public Table<MyTable> MyTables { get; set; }\n}\n\npublic class MyTable\n{\n    [Column(Name = "ID")]\n    public int Id { get; set; }\n    [Column(Name = "NAME")]\n    public string Name { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        using (var context = new MyContext())\n        {\n            var query = context.MyTables.Where(mt => mt.Name == "John");\n            foreach (var item in query)\n            {\n                Console.WriteLine(item.Id + " " + item.Name);\n            }\n        }\n    }\n}