using NHibernate;\nusing NHibernate.Cfg;\n\n// Create a configuration object\nvar cfg = new Configuration();\n\n// Set the database connection string\ncfg.DataBaseIntegration(db => db.ConnectionString = "Server=myServer;Database=myDatabase;User Id=myUser;Password=myPassword;");\n\n// Add mappings for your entities\ncfg.AddAssembly(typeof(MyEntity).Assembly);\n\n// Create a session factory\nvar sessionFactory = cfg.BuildSessionFactory();\n\n// Create a session\nvar session = sessionFactory.OpenSession();\n\n// Use the session to interact with your database