using System;\nusing System.Reflection;\n\n// ...\n\nforeach (var assembly in AppDomain.CurrentDomain.GetAssemblies())\n{\n    var assemblyName = assembly.GetName().Name;\n    var assemblyVersion = assembly.GetName().Version.ToString();\n\n    // Get the build date and time\n    var attribute = (AssemblyMetadataAttribute)assembly.GetCustomAttributes(typeof(AssemblyMetadataAttribute), false).FirstOrDefault();\n    var buildDateTime = attribute == null ? DateTime.MinValue : attribute.Date;\n\n    Console.WriteLine($"Assembly: {assemblyName}, Version: {assemblyVersion}, Build Date/Time: {buildDateTime}");\n}