DirectorySearcher searcher = new DirectorySearcher(entry);\nsearcher.PropertiesToLoad.Add("objectSid");\nsearcher.PropertiesToLoad.Add("primaryGroupID");\n\nSearchResult result = searcher.FindOne();\n\nbyte[] sidBytes = (byte[])result.Properties["objectSid"][0];\nint primaryGroupId = (int)result.Properties["primaryGroupID"][0];\n\nSecurityIdentifier sid = new SecurityIdentifier(sidBytes, 0);\nstring sidValue = sid.ToString();\n\n// Construct the full SID with RID\nstring fullSidValue = $"{sidValue}-{primaryGroupId}";