using System;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\n[ComVisible(true)]\n[ClassInterface(ClassInterfaceType.AutoDispatch)]\npublic class MyComServer : MarshalByRefObject\n{\n    public MyComServer()\n    {\n        // Initialize your VB6 COM object here\n    }\n\n    // Add your COM-visible methods here\n    public void MyMethod()\n    {\n        MessageBox.Show("Hello from C# COM server!");\n    }\n}\n\nclass Program\n{\n    [STAThread]\n    static void Main(string[] args)\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n\n        var comServer = new MyComServer();\n        Marshal.AddRef(comServer);\n        Application.Run(); // Keep the process running\n    }\n}