using System.Security.AccessControl;\nusing System.IO;\n\n// Get the file's current security settings\nFileSecurity fileSecurity = File.GetAccessControl("path/to/file.txt");\n\n// Create a new rule granting read access to the "Domain Users" group\nFileSystemAccessRule newRule = new FileSystemAccessRule(\n    "Domain Users",\n    FileSystemRights.Read,\n    AccessControlType.Allow\n);\n\n// Add the new rule to the file's security settings\nfileSecurity.AddAccessRule(newRule);\n\n// Save the changes\nFile.SetAccessControl("path/to/file.txt", fileSecurity);