using System.Data.OracleClient;\n\n// assume you have a DataTable dt containing the data to be inserted\nOracleConnection connection = new OracleConnection("Your Connection String");\nconnection.Open();\n\nOracleCommand command = new OracleCommand("INSERT INTO YourTable (Column1, Column2) VALUES (:param1, :param2)", connection);\ncommand.Parameters.Add(":param1", OracleType.VarChar, 20, "Column1");\ncommand.Parameters.Add(":param2", OracleType.VarChar, 20, "Column2");\n\nOracleDataAdapter adapter = new OracleDataAdapter();\nadapter.InsertCommand = command;\n\nadapter.UpdateBatchSize = 1000; // set the batch size to 1000\nadapter.Update(dt);