using Facebook;\n\n// Assuming you have the FacebookClient initialized with your AppId and AppSecret\nvar facebookClient = new FacebookClient();\n\n// Try to get the user's access token\nvar accessToken = facebookClient.AccessToken;\n\n// If the access token is not null, the user is logged in\nif (accessToken != null)\n{\n    // You can also verify the token by calling the DebugToken endpoint\n    var debugTokenResult = facebookClient.Get("/debug_token", new { input_token = accessToken });\n    if (debugTokenResult.Result.ContainsKey("is_valid") && (bool)debugTokenResult.Result["is_valid"])\n    {\n        // The access token is valid, so the user is logged in\n    }\n}