using Microsoft.EntityFrameworkCore;\nusing Microsoft.EntityFrameworkCore.InMemory;\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyEntity> MyEntities { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseInMemoryDatabase(databaseName: "TestDatabase");\n    }\n}\n\n[TestMethod]\npublic void TestMyMethod()\n{\n    // Create a new instance of the DbContext\n    var dbContext = new MyDbContext();\n\n    // Add some entities to the database\n    dbContext.MyEntities.Add(new MyEntity { Id = 1, Name = "Entity 1" });\n    dbContext.MyEntities.Add(new MyEntity { Id = 2, Name = "Entity 2" });\n    dbContext.SaveChanges();\n\n    // Use the DbContext in your test\n    var myMethod = new MyMethod(dbContext);\n    var result = myMethod.DoSomething();\n\n    // Assert the result\n    Assert.AreEqual(2, result.Count);\n}