public void RemoveUsersFromRoles(string[] usernames, string[] rolenames)\n{\n    using (var db = new YourDataContext())\n    {\n        var usersToRemove = db.Users.Where(u => usernames.Contains(u.Username));\n        var rolesToRemove = db.Roles.Where(r => rolenames.Contains(r.Rolename));\n\n        var usersInRolesToRemove = db.UsersInRoles\n            .Where(uir => usersToRemove.Contains(uir.User) && rolesToRemove.Contains(uir.Role));\n\n        db.UsersInRoles.DeleteAllOnSubmit(usersInRolesToRemove);\n        db.SubmitChanges();\n    }\n}