using System.IO.Compression;\n\n// Download the zip file to a temporary location\nstring tempZipPath = Path.GetTempFileName();\nusing (var wc = new WebClient())\n{\n    wc.DownloadFile("https://example.com/images.zip", tempZipPath);\n}\n\n// Extract the zip file to the images directory\nstring imagesPath = Path.Combine(Application.UserAppDataPath, "Images");\nZipFile.ExtractToDirectory(tempZipPath, imagesPath);\n\n// Clean up the temporary zip file\nFile.Delete(tempZipPath);