using System.IO;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileName = "cheese.exe";\n        string[] paths = Directory.EnumerateFiles(@"C:\", fileName, SearchOption.AllDirectories);\n\n        Parallel.ForEach(paths, path =>\n        {\n            Console.WriteLine($"Found {fileName} at {Path.GetDirectoryName(path)}");\n        });\n    }\n}