public partial class AjaxifiedContent : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string controlId = Request.QueryString["controlId"];\n        Control control = FindControl(controlId);\n\n        if (control != null)\n        {\n            // Render the control and its contents\n            control.RenderControl(new HtmlTextWriter(Response.OutputStream));\n        }\n    }\n}