string password = "mysecretpassword";\nstring dbFile = "mydatabase.db";\n\n// Encrypt the database file\nbyte[] encryptedData = SQLiteEncryptor.Encrypt(File.ReadAllBytes(dbFile), password);\nFile.WriteAllBytes(dbFile, encryptedData);\n\n// Decrypt the database file\nbyte[] decryptedData = SQLiteEncryptor.Decrypt(File.ReadAllBytes(dbFile), password);\nFile.WriteAllBytes(dbFile, decryptedData);