[WebMethod]\npublic static void ProxyRequest(string id, string uname, string upass)\n{\n    // Create a new request to the target server\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://www.somedomain.com/WebSite1/Default2.aspx?id=" + id);\n    request.Method = "POST";\n    request.ContentType = "application/x-www-form-urlencoded";\n    request.Headers.Add("uname", uname);\n    request.Headers.Add("upass", upass);\n\n    // Get the response from the target server\n    HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n    // Return the response to the client\n    Response.Write(response.ReadToEnd());\n}