using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Specify the input file paths\n        string[] inputFilePaths = new string[] \n        {\n            @"C:\path\to\file1.txt",\n            @"C:\path\to\file2.txt",\n            @"C:\path\to\file3.txt",\n            @"C:\path\to\file4.txt",\n            @"C:\path\to\file5.txt"\n        };\n\n        // Specify the output file path\n        string outputFilePath = @"C:\path\to\output.txt";\n\n        // Read and write file contents\n        using (StreamWriter outputFile = new StreamWriter(outputFilePath))\n        {\n            foreach (string inputFilePath in inputFilePaths)\n            {\n                using (StreamReader inputFile = new StreamReader(inputFilePath))\n                {\n                    outputFile.Write(inputFile.ReadToEnd());\n                }\n            }\n        }\n\n        Console.WriteLine("Files merged successfully.");\n    }\n}