using System.Data.SqlClient;\n\n// create a SqlConnection object\nSqlConnection connection = new SqlConnection("Data Source=myServer;Initial Catalog=myDatabase;Integrated Security=True");\n\n// open the connection\nconnection.Open();\n\n// retrieve statistics\nIDictionary statistics = connection.RetrieveStatistics();\n\n// print the statistics\nforeach (DictionaryEntry entry in statistics)\n{\n    Console.WriteLine(entry.Key + ": " + entry.Value);\n}\n\n// close the connection\nconnection.Close();