var dictionary = new Dictionary<string, string>\n{\n    {"Item1", ""},\n    {"Item2", ""},\n    {"Item3", ""}\n};\n\nvar words = new List<string> { "word1", "word2", "word3", "word4", "word5" };\n\nvar currentItem = "Item1";\n\nforeach (var word in words)\n{\n    if (dictionary[currentItem].Length + word.Length + 1 > 50)\n    {\n        // move to the next item\n        switch (currentItem)\n        {\n            case "Item1":\n                currentItem = "Item2";\n                break;\n            case "Item2":\n                currentItem = "Item3";\n                break;\n            default:\n                throw new InvalidOperationException("Dictionary is full");\n        }\n    }\n\n    dictionary[currentItem] += word + " ";\n}