private void ListFiles()\n{\n    // Create a CheckedListBox\n    CheckedListBox listBox = new CheckedListBox();\n    listBox.Location = new Point(10, 10);\n    listBox.Size = new Size(300, 200);\n    this.Controls.Add(listBox);\n\n    // Get files in a directory\n    string[] files = Directory.GetFiles(@"C:\Your\Directory");\n\n    // Add files to the list box\n    foreach (string file in files)\n    {\n        listBox.Items.Add(Path.GetFileName(file));\n    }\n}\n\nprivate void GetSelectedFiles()\n{\n    // Get the selected files\n    CheckedListBox listBox = (CheckedListBox)this.Controls[0];\n    foreach (int index in listBox.CheckedIndices)\n    {\n        string file = listBox.Items[index].ToString();\n        // Perform operations with the selected file\n        Console.WriteLine(file);\n    }\n}