var list = new[]\n{\n    new { theID = 1, theGroup = 2, theValue = "Red" },\n    new { theID = 2, theGroup = 2, theValue = "Blue" },\n    new { theID = 3, theGroup = 2, theValue = "Green" },\n    new { theID = 1, theGroup = 3, theValue = "10" },\n    new { theID = 2, theGroup = 3, theValue = "24" },\n    new { theID = 3, theGroup = 3, theValue = "30" },\n    new { theID = 1, theGroup = 4, theValue = "1" },\n    new { theID = 2, theGroup = 4, theValue = "2" },\n    new { theID = 3, theGroup = 4, theValue = "3" },\n};\n\nvar result = list.GroupBy(x => x.theID)\n                 .Select(g => new\n                 {\n                     theID = g.Key,\n                     CHOICE = g.Where(x => x.theGroup == 2).FirstOrDefault().theValue,\n                     COUNT = g.Where(x => x.theGroup == 3).FirstOrDefault().theValue,\n                     SORT = g.Where(x => x.theGroup == 4).FirstOrDefault().theValue\n                 });