string[] lines = File.ReadAllLines("C:\\Temp\\Data.dat");\n\nusing (SqlBulkCopy bulkCopy = new SqlBulkCopy(myDataContext.Connection))\n{\n    bulkCopy.DestinationTableName = "MyDataObjects";\n    bulkCopy.BatchSize = 1000;\n\n    using (IDataReader reader = new MyDataObjectReader(lines))\n    {\n        bulkCopy.WriteToServer(reader);\n    }\n}\n\npublic class MyDataObjectReader : IDataReader\n{\n    private string[] _lines;\n    private int _currentIndex;\n\n    public MyDataObjectReader(string[] lines)\n    {\n        _lines = lines;\n        _currentIndex = 0;\n    }\n\n    public bool Read()\n    {\n        return _currentIndex < _lines.Length;\n    }\n\n    public object GetValue(int i)\n    {\n        // Parse the line and return the value for the current column\n        MyDataObject obj = ParseObject(_lines[_currentIndex]);\n        return obj.GetValue(i);\n    }\n\n    // Implement the rest of the IDataReader interface...\n}