bool IsObjectVisible(GameObject viewer, GameObject target) {\n    // Calculate direction vector from viewer to target\n    Vector3 directionToTarget = (target.position - viewer.position).normalized;\n\n    // Calculate angle between direction vector and viewer's facing direction\n    float angle = Vector3.Angle(viewer.facingDirection, directionToTarget);\n\n    // Check if angle is within field of view\n    if (angle >= -viewer.fieldOfView / 2 && angle <= viewer.fieldOfView / 2) {\n        // Perform raycast to check for obstructing objects\n        RaycastHit hit;\n        if (Physics.Raycast(viewer.position, directionToTarget, out hit, viewer.viewRange)) {\n            // If raycast hits the target object, it's visible\n            return hit.transform == target.transform;\n        }\n    }\n\n    // Target object is not visible\n    return false;\n}