// In your controller\npublic ActionResult MyAction()\n{\n    // Create a model to hold the data\n    var model = new MyModel\n    {\n        InitialHour = "09:00",\n        FinalHour = "17:00",\n        Weekday = "Monday"\n    };\n\n    // Serialize the model to a JSON string\n    var jsonData = JsonConvert.SerializeObject(model);\n\n    // Encode the JSON string to prevent XSS attacks\n    var encodedData = HttpUtility.HtmlEncode(jsonData);\n\n    // Pass the encoded data to the view\n    ViewBag.Data = encodedData;\n\n    return View();\n}\n\n// In your view\n@Html.Hidden("Data", ViewBag.Data)\n\n// In your JavaScript code, you can retrieve the data like this:\nvar data = $('#Data').val();\nvar jsonData = JSON.parse(data);