public static List<T> MergeLists<T>(params List<T>[] lists)\n{\n    int totalElements = lists.Sum(l => l.Count);\n    List<T> result = new List<T>(totalElements);\n    int[] indices = new int[lists.Length];\n\n    for (int i = 0; i < totalElements; i++)\n    {\n        int minIndex = -1;\n        int minListIndex = -1;\n\n        for (int j = 0; j < lists.Length; j++)\n        {\n            if (indices[j] < lists[j].Count)\n            {\n                if (minIndex == -1 || indices[j] < minIndex)\n                {\n                    minIndex = indices[j];\n                    minListIndex = j;\n                }\n            }\n        }\n\n        result.Add(lists[minListIndex][indices[minListIndex]++]);\n    }\n\n    return result;\n}