using System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string[] wordsToMatch = new string[] { "Some", "Text", "Here" };\n        string[] inputStrings = new string[] { "Some Text here", "some text Here" };\n\n        foreach (string inputString in inputStrings)\n        {\n            string outputString = inputString;\n            foreach (string word in wordsToMatch)\n            {\n                outputString = Regex.Replace(outputString, \n                                             @"\b(" + Regex.Escape(word) + @")\b", \n                                             match => $"<dfn title=\"Definition of {match.Value}\">{match.Value}</dfn>", \n                                             RegexOptions.IgnoreCase);\n            }\n            System.Console.WriteLine(outputString);\n        }\n    }\n}