// Example code to get you started with profiling\nusing System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Start profiling\n        var profiler = new Stopwatch();\n        profiler.Start();\n\n        // Your application code here\n        MyMethod();\n\n        // Stop profiling\n        profiler.Stop();\n        Console.WriteLine($"Elapsed time: {profiler.ElapsedMilliseconds}ms");\n    }\n\n    static void MyMethod()\n    {\n        // Simulate some work\n        for (int i = 0; i < 1000000; i++)\n        {\n            // Do something\n        }\n    }\n}