from Import_Table t in db.Import_Tables\njoin Import_CheckoutHistory ich in db.Import_CheckoutHistories on t.Import_CheckoutHistoryID equals ich.Import_CheckoutHistoryID into ichJoin\nfrom ich in ichJoin.DefaultIfEmpty()\njoin System_User su in db.System_Users on ich.System_UserID equals su.System_UserID into suJoin\nfrom su in suJoin.DefaultIfEmpty()\nselect new {\n    CheckedOutUser = (!t.IsCheckedOut) ? "--" : su.FirstName + " " + su.LastName,\n    CheckedOutUserID = (!t.IsCheckedOut) ? 0 : su.System_UserID\n};