protected override void OnKeyDown(KeyEventArgs e)\n{\n    if (e.KeyCode == Keys.Back || e.KeyCode == Keys.ControlKey)\n    {\n        base.OnKeyDown(e);\n        return;\n    }\n\n    if (String.IsNullOrEmpty(this._ValidCharExpression))\n    {\n        base.OnKeyDown(e);\n    }\n    else\n    {\n        bool isValidChar = true;\n\n        // Check if the pressed key is a character key\n        if (e.KeyCode >= Keys.A && e.KeyCode <= Keys.Z)\n        {\n            // Check if the character matches the regex pattern\n            isValidChar = Regex.Match(e.KeyCode.ToString(), this._ValidCharExpression).Success;\n        }\n\n        if (isValidChar)\n        {\n            base.OnKeyDown(e);\n        }\n        else\n        {\n            e.Handled = true;\n        }\n    }\n}