using ImageMagick;\n\n// Load the image\nusing (MagickImage image = new MagickImage("input.png"))\n{\n    // Define the distortion points\n    PointF[] points = new[] { new PointF(0, -100), new PointF(image.Width, 0), new PointF(image.Width, image.Height), new PointF(0, image.Height) };\n\n    // Apply the distortion\n    image.Distort(DistortMethod.Perspective, points);\n\n    // Save the transformed image\n    image.Write("output.png");\n}