protected void Repeater_ItemDataBound(object sender, RepeaterItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        Literal itemTextLiteral = (Literal)e.Item.FindControl("ItemTextLiteral");\n        if (IsLink)\n        {\n            itemTextLiteral.Text = string.Format("<a href=\"{0}\">{1}</a>", GetItemLink(), GetItemText());\n        }\n        else\n        {\n            itemTextLiteral.Text = GetItemText();\n        }\n    }\n}