using ExifLib;\n\n// Load the JPEG file\nusing (var fileStream = new FileStream("image.jpg", FileMode.Open, FileAccess.ReadWrite))\n{\n    // Create an ExifReader instance\n    var exifReader = new ExifReader(fileStream);\n\n    // Get the Exif data\n    var exifData = exifReader.GetExifData();\n\n    // Modify the Exif data (e.g., add a new tag)\n    exifData.AddTag(ExifTag.Artist, "John Doe");\n\n    // Create an ExifWriter instance\n    var exifWriter = new ExifWriter(fileStream);\n\n    // Write the modified Exif data back to the file\n    exifWriter.WriteExifData(exifData);\n}