string tempDir = Path.GetTempPath();\nstring newTempDir = Path.Combine(tempDir, "MyAppTemp");\n\nif (!Directory.Exists(newTempDir))\n{\n    Directory.CreateDirectory(newTempDir);\n    DirectorySecurity ds = Directory.GetAccessControl(newTempDir);\n    ds.AddAccessRule(new FileSystemAccessRule("IUSR_ComputerName", FileSystemRights.Write, AccessControlType.Allow));\n    Directory.SetAccessControl(newTempDir, ds);\n}