using Microsoft.EntityFrameworkCore;\nusing System.Linq;\n\npublic class MyContext : DbContext\n{\n    public DbSet<MyEntity> MyEntities { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseSqlServer(@"Server=myserver;Database=mydatabase;User Id=myuser;Password=mypassword;");\n    }\n}\n\npublic class MyEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public ICollection<MyRelatedEntity> RelatedEntities { get; set; }\n}\n\npublic class MyRelatedEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public int MyEntityId { get; set; }\n    public MyEntity MyEntity { get; set; }\n}\n\nvar context = new MyContext();\nvar result = context.MyEntities\n    .Include(e => e.RelatedEntities)\n    .Where(e => e.Name.Contains("searchterm"))\n    .ToList();