private void SendMailToAdminToApprove(string email_, string name_)\n{\n    MailMessage msg = new MailMessage();\n    msg.From = new MailAddress("address@domain.com", "Person's Name");\n    msg.To.Add(new MailAddress("a@gmail.com", "Adam"));\n    msg.Subject    = "Message Subject";\n    msg.Body       = "Mail body content";\n    msg.IsBodyHtml = true;\n    msg.Priority   = MailPriority.High;\n\n    try\n    {\n        SmtpClient c = new SmtpClient("smtp.gmail.com"); // Use Gmail's SMTP server\n        c.Port = 587; // Use Gmail's SMTP port\n        c.EnableSsl = true; // Enable SSL encryption\n        c.Credentials = new NetworkCredential("your_email@gmail.com", "your_password"); // Use your Gmail credentials\n        c.Send(msg);\n    }\n    catch (Exception ex)\n    {\n        Console.Write("Error: " + ex.Message);\n    }\n}