// Server side\nusing System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Sockets;\n\nclass FileServer\n{\n    private TcpListener _listener;\n\n    public FileServer(int port)\n    {\n        _listener = new TcpListener(IPAddress.Any, port);\n    }\n\n    public void Start()\n    {\n        _listener.Start();\n        Console.WriteLine("Server started. Waiting for connections...");\n\n        while (true)\n        {\n            TcpClient client = _listener.AcceptTcpClient();\n            Console.WriteLine("Client connected");\n\n            // Read file from client\n            NetworkStream stream = client.GetStream();\n            byte[] buffer = new byte[4096];\n            int bytesRead;\n            using (FileStream fileStream = new FileStream("received_file.bin", FileMode.Create, FileAccess.Write))\n            {\n                while ((bytesRead = stream.Read(buffer, 0, buffer.Length)) > 0)\n                {\n                    fileStream.Write(buffer, 0, bytesRead);\n                }\n            }\n\n            client.Close();\n        }\n    }\n}\n\n// Client side\nusing System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Sockets;\n\nclass FileClient\n{\n    private TcpClient _client;\n\n    public FileClient(string serverAddress, int port)\n    {\n        _client = new TcpClient();\n        _client.Connect(serverAddress, port);\n    }\n\n    public void SendFile(string filePath)\n    {\n        // Open file and send its contents\n        using (FileStream fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            NetworkStream stream = _client.GetStream();\n            byte[] buffer = new byte[4096];\n            int bytesRead;\n            while ((bytesRead = fileStream.Read(buffer, 0, buffer.Length)) > 0)\n            {\n                stream.Write(buffer, 0, bytesRead);\n            }\n        }\n\n        _client.Close();\n    }\n}