using System.ServiceModel.Channels;\n\n// create a new instance of the service client\nvar client = new WSWrapperClient();\n\n// create a new cookie\nvar cookie = new Cookie(name, value, path, domain);\n\n// create a new HttpRequestMessageProperty\nvar httpRequestProperty = new HttpRequestMessageProperty();\nhttpRequestProperty.Headers.Add("Cookie", cookie.ToString());\n\n// add the HttpRequestMessageProperty to the client's request\nusing (var scope = new OperationContextScope(client.InnerChannel))\n{\n    OperationContext.Current.OutgoingMessageProperties[HttpRequestMessageProperty.Name] = httpRequestProperty;\n\n    // call the service method\n    client.Test();\n}