using System;\nusing System.Runtime.InteropServices;\n\npublic class Program\n{\n    [DllImport("kernel32.dll", SetLastError = true)]\n    public static extern bool GetVolumeInformation(\n        string lpRootPathName,\n        StringBuilder lpVolumeNameBuffer,\n        uint nVolumeNameSize,\n        out uint lpSerialNumber,\n        out uint lpMaximumComponentLength,\n        out uint lpFileSystemFlags,\n        StringBuilder lpFileSystemNameBuffer,\n        uint nFileSystemNameSize\n    );\n\n    public static void Main()\n    {\n        string path = @"\\myserver\users\brian\public\music";\n\n        uint serialNumber;\n        uint maximumComponentLength;\n        uint fileSystemFlags;\n        StringBuilder volumeNameBuffer = new StringBuilder(256);\n        StringBuilder fileSystemNameBuffer = new StringBuilder(256);\n\n        if (GetVolumeInformation(path, volumeNameBuffer, 256, out serialNumber, out maximumComponentLength, out fileSystemFlags, fileSystemNameBuffer, 256))\n        {\n            Console.WriteLine("Volume Serial Number: " + serialNumber);\n        }\n        else\n        {\n            Console.WriteLine("Error: " + Marshal.GetLastWin32Error());\n        }\n    }\n}